import { useState } from "react"
import { Link, Outlet, useLocation } from "react-router-dom"
import { Menu, X, Globe, LogOut } from "lucide-react"
import { navigationData, footerData } from "../data/navigationData"
import Logo from "./Logo"
import { useLogout, useAuth } from "../hooks/useAuth"
import { ThemeProvider } from "../context/ThemeContext"
import { SimpleThemeToggle } from "../ui/ThemeToggle"


export default function AppLayout() {
  const [isMenuOpen, setIsMenuOpen] = useState(false)
  const location = useLocation()

  const navigation = navigationData

  const { data, isAuthenticated, isLoading } = useAuth()
  const logoutMutation = useLogout()

  const handleLogout = () => {
    logoutMutation.mutate()
  }

  const isActive = (path) => location.pathname === path

  return (
    <ThemeProvider defaultTheme="system" storageKey="growtly-theme">
      <div className="min-h-screen bg-white dark:bg-gray-900 transition-colors">
        {/* Navigation Header */}
        <nav className="bg-white dark:bg-gray-900 shadow-sm border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50 transition-colors">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              {/* Logo */}
              <div className="flex items-center">
                <Link to="/" className="flex items-center space-x-2 group">
                  <Logo />
                  <span className="text-xl font-bold text-gray-900 dark:text-white group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors">
                    Growtly
                  </span>
                </Link>
              </div>


              {/* Desktop Navigation */}
              <div className="hidden md:block">
                <div className="ml-10 flex items-baseline space-x-4">
                  {navigation.map((item) => (
                    <Link
                      key={item.name}
                      to={item.href}
                      className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${isActive(item.href)
                        ? "bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300"
                        : "text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-50 dark:hover:bg-gray-800"
                        }`}
                    >
                      {item.name}
                    </Link>
                  ))}
                </div>
              </div>

              {/* Desktop Auth Buttons & Theme Toggle */}
              <div className="hidden md:flex items-center space-x-4">
                <SimpleThemeToggle />
                {isLoading ? (
                  <div className="w-20 h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
                ) :
                  isAuthenticated ? (
                    <div className="flex items-center space-x-3">
                      <div className="flex items-center space-x-2 text-gray-700 dark:text-gray-200">
                        {data?.user.imageUrl ? (
                          <img
                            src={data.user.imageUrl}
                            alt="User avatar"
                            className="w-4 h-4 rounded-full object-cover"
                          />
                        ) : (
                          // <User className="w-4 h-4" /> // Fallback to icon if no image URL
                          <img
                            src={"data:image/png;base64,"}
                            alt="User avatar"
                            className="w-4 h-4 rounded-full object-cover"
                          />
                        )}
                        <span className="text-sm font-medium">{data?.user.name}</span>
                      </div>
                      <button
                        onClick={handleLogout}
                        disabled={logoutMutation.isPending}
                        className="flex items-center space-x-1 text-gray-600 dark:text-gray-300 hover:text-red-600 dark:hover:text-red-400 px-3 py-2 rounded-md text-sm font-medium transition-colors disabled:opacity-50"
                        title="Sign out"
                      >
                        <LogOut className="w-4 h-4" />
                        <span>{logoutMutation.isPending ? "Signing out..." : "Sign out"}</span>
                      </button>
                    </div>
                  ) : (
                    <>
                      <Link
                        to="/login"
                        className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors"
                      >
                        Sign In
                      </Link>
                      <Link
                        to="/signup"
                        className="bg-blue-600 hover:bg-blue-700 dark:bg-blue-500 dark:hover:bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium transition-colors"
                      >
                        Get Started
                      </Link>
                    </>
                  )}
              </div>

              {/* Mobile menu button */}
              <div className="md:hidden flex items-center space-x-2">
                <SimpleThemeToggle />
                <button
                  onClick={() => setIsMenuOpen(!isMenuOpen)}
                  className="inline-flex items-center justify-center p-2 rounded-md text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
                >
                  {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
              </div>
            </div>
          </div>

          {/* Mobile Navigation Menu */}
          {isMenuOpen && (
            <div className="md:hidden">
              <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-700 transition-colors">
                {navigation.map((item) => (
                  <Link
                    key={item.name}
                    to={item.href}
                    className={`block px-3 py-2 rounded-md text-base font-medium transition-colors ${isActive(item.href)
                      ? "bg-blue-100 dark:bg-blue-900 text-blue-700 dark:text-blue-300"
                      : "text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-50 dark:hover:bg-gray-800"
                      }`}
                    onClick={() => setIsMenuOpen(false)}
                  >
                    {item.name}
                  </Link>
                ))}
                <div className="pt-4 pb-3 border-t border-gray-200 dark:border-gray-700">
                  {isLoading ? (
                    <div className="px-3 py-2">
                      <div className="w-32 h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse"></div>
                    </div>
                  ) : isAuthenticated ? (
                    <>
                      <div className="px-3 py-2 flex items-center space-x-2 text-gray-700 dark:text-gray-200">
                        {data?.user.imageUrl ? (
                          <img src={data.user.imageUrl} alt={data.user.name} className="w-8 h-8 rounded-full" />
                        ) : (
                          <img src="data:image/png;base64," alt="Default Avatar" className="w-8 h-8 rounded-full" />
                        )}
                        <span className="text-sm font-medium">{data?.user.name}</span>
                      </div>
                      <button
                        onClick={() => {
                          handleLogout()
                          setIsMenuOpen(false)
                        }}
                        disabled={logoutMutation.isPending}
                        className="w-full text-left flex items-center space-x-2 px-3 py-2 rounded-md text-base font-medium text-gray-600 dark:text-gray-300 hover:text-red-600 dark:hover:text-red-400 hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors disabled:opacity-50"
                      >
                        <LogOut className="w-4 h-4" />
                        <span>{logoutMutation.isPending ? "Signing out..." : "Sign out"}</span>
                      </button>
                    </>
                  ) : (
                    <>
                      <Link
                        to="/login"
                        className="block px-3 py-2 rounded-md text-base font-medium text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        Sign In
                      </Link>
                      <Link
                        to="/signup"
                        className="block px-3 py-2 rounded-md text-base font-medium bg-blue-600 dark:bg-blue-500 text-white hover:bg-blue-700 dark:hover:bg-blue-600 mt-2 transition-colors"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        Get Started
                      </Link>
                    </>
                  )}
                </div>
              </div>
            </div>
          )}
        </nav>

        {/* Main Content */}
        <main className="flex-1">
          <Outlet />
        </main>

        {/* Footer */}
        <footer className="bg-gray-900 dark:bg-gray-950 text-white transition-colors">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
              {/* Company Info */}
              <div className="col-span-1 md:col-span-2">
                <div className="flex items-center space-x-2 mb-4">
                  <Logo withLink={true} />
                  <span className="text-xl font-bold dark:text-white">
                    {footerData.company.name}
                  </span>
                </div>

                <p className="text-gray-300 dark:text-gray-400 mb-4 max-w-md">{footerData.company.description}</p>
                <div className="flex space-x-4">
                  <Globe className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-300 dark:text-gray-400 text-sm">{footerData.company.tagline}</span>
                </div>
              </div>

              {/* Quick Links */}
              <div>
                <h3 className="text-lg font-semibold mb-4">Platform</h3>
                <ul className="space-y-2">
                  {footerData.links.platform.map((link) => (
                    <li key={link.name}>
                      <Link
                        to={link.href}
                        className="text-gray-300 dark:text-gray-400 hover:text-white transition-colors"
                      >
                        {link.name}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Support */}
              <div>
                <h3 className="text-lg font-semibold mb-4">Support</h3>
                <ul className="space-y-2">
                  {footerData.links.support.map((link) => (
                    <li key={link.name}>
                      <Link
                        to={link.href}
                        className="text-gray-300 dark:text-gray-400 hover:text-white transition-colors"
                      >
                        {link.name}
                      </Link>
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            <div className="border-t border-gray-800 dark:border-gray-700 mt-8 pt-8 flex flex-col md:flex-row justify-between items-center">
              <p className="text-gray-400 text-sm">{footerData.copyright}</p>
              <div className="flex items-center space-x-4 mt-4 md:mt-0">
                <span className="text-gray-400 text-sm">{footerData.taglineBottom}</span>
              </div>
            </div>
          </div>
        </footer>
      </div>
    </ThemeProvider>
  )
}
